import React, { useState,useEffect } from "react";
import axios from "axios";
import Spinner from "./Spinner";
const API_KEY = process.env.REACT_APP_GIPHY_API_KEY;
export default function Random() {
  const [gif, setGif] = useState("");
const [loading,setLoading] =useState(false);
  async function fetchData() {
    setLoading(true);
    const url = `https://api.giphy.com/v1/gifs/random?api_key=${API_KEY}`;
   const {data}=  await axios.get(url);
   const imageApi = data.data.images.downsized_large.url;
   setGif(imageApi);
   setLoading(false);
  }
  useEffect(()=>{
    fetchData();
  },[]);
  function clickHandler() {
    fetchData();
  }
  return (
    <div className=" flex flex-col w-1/2 mt-[15px] items-center rounded-md bg-transparent gap-y-5 border border-b-gray-50 justify-center hover:shadow-lg bg-gradient-to-r from-cyan-600 to-blue-500 ...">
      <h1 className=" text-bold text-2xl uppercase underline text-gray-600 mt-[30px] ">
        A Random GIF
      </h1>
      {
        loading ?
         (<Spinner />) : (

          <img src={gif} width="450px"  className=" rounded-lg "
          height={300}
          />
        ) 
      }
      <button
        className="w-10/12 mb-[20px]  border border-bg-gray-500 text-lg py-2 rounded-lg hover:bg-black text-white transition-all duration-200 "
        onClick={clickHandler}
      >
        Generate
      </button>
    </div>
  );
}
