import React, { useState,useEffect } from "react";
import axios from "axios";
const API_KEY = process.env.REACT_APP_GIPHY_API_KEY;

export default function Random() {
  const [gif, setGif] = useState("");

  async function fetchData() {
    const url = `https://api.giphy.com/v1/gifs/random?api_key=${API_KEY}`;
   const {data}=  await axios.get(url);
   const imageApi = data.data.images.downsized_large.url;
   setGif(imageApi);
  }
  useEffect(()=>{
    fetchData();
  },[]);
  function clickHandler() {
    fetchData();
  }
  return (
    <div className=" flex flex-col w-1/2 h-[400px] mt-[15px] items-center rounded-md bg-transparent gap-y-5 border border-b-gray-50 justify-center">
      <h1 className=" text-bold text-2xl uppercase underline text-gray-600  ">
        A Random GIF
      </h1>
      <img src={gif} width="450"  className=" "
      height="300"
      />
      <button
        className="w-10/12  border border-bg-gray-500 text-lg py-2 rounded-lg hover:bg-black text-white transition-all duration-200 "
        onClick={clickHandler}
      >
        Generate
      </button>
    </div>
  );
}
