import React, { useState } from "react";
import axios from "axios";
const API_KEY = process.env.REACT_APP_GIPHY_API_KEY;

export default function Random() {
  const [gif, setGif] = useState("");

  async function fetchData() {
    const url = `https://api.giphy.com/v1/gifs/random?api_key=${API_KEY}`;
   const output=  await axios.get(url);
   console.log(output);
  }
  useEffect(()=>{
    fetchData();
  },[]);
  function clickHandler() {}

  return (
    <div className=" flex flex-col w-1/2 h-[450px] mt-[15px] items-center rounded-md bg-transparent gap-y-5 border border-b-gray-50 justify-center">
      <h1 className=" text-bold text-2xl uppercase underline text-gray-600  ">
        {" "}
        A Random GIF
      </h1>
      <img src={gif} width={450} />
      <button
        className="w-10/12  border border-bg-gray-500 text-lg py-2 rounded-lg hover:bg-gray-500 "
        onClick={clickHandler}
      >
        Generate
      </button>
    </div>
  );
}
